import os, base64, shutil

files = {
    'parsers/care_content.py': '',
}
for path, b64 in files.items():
    os.makedirs(os.path.dirname(path) or '.', exist_ok=True)
    with open(path, 'wb') as f:
        f.write(base64.b64decode(b64))
    print(f'‚úÖ Written: {path}')
for root, dirs, _ in os.walk('.'):
    for d in dirs:
        if d == '__pycache__':
            try: shutil.rmtree(os.path.join(root, d)); print(f'üóëÔ∏è  {os.path.join(root, d)}')
            except: pass
print('\n‚úÖ Done! Run: python -m streamlit run app.py')